import React, { Component } from "react";
import Header from "./components/header";
import "./App.css";
import CalculatorList from "./components/calculator_list";

//Now we want to exchange data between two child components: Counters and Navbar (to display number of items)
// There is no parent-child relationship between the two
//We need to raise the state of Counters component up to App

class App extends Component {
  state = {
    counters: [
      { id: 1, value: 4 },
      // { id: 2, value: 0 },
      // { id: 3, value: 0 },
      // { id: 4, value: 0 },
    ],
  };

  handleIncrement = (counter) => {
    // this.state.count++; // we cannot change value of count directly like this - React will not be aware that it changed
    const counters = [...this.state.counters]; // ... is cloning array of counters from state to var "counters"
    const index = counters.indexOf(counter); // find index of counter within counters (in state)
    counters[index] = { ...counter }; //to clone the counter at exact location - counter object in state remains unchanged
    counters[index].value++;
    this.setState({ counters }); //update state
  };

  handleDecrement = (counter) => {
    // this.state.count++; // we cannot change value of count directly like this - React will not be aware that it changed
    const counters = [...this.state.counters]; // ... is cloning array of counters from state to var "counters"
    const index = counters.indexOf(counter); // find index of counter within counters (in state)
    counters[index] = { ...counter }; //to clone the counter at exact location - counter object in state remains unchanged
    counters[index].value -= counters[index].value > 0 ? 1 : 0;
    // counters[index].value === 0 ? counters[index].value-- : counters[index].value;
    // classes += this.props.counter.value === 0 ? "warning" : "primary";
    this.setState({ counters }); //update state
  };

  handleDelete = (counterId) => {
    // console.log("Delete event handled.", counterId);
    const counters = this.state.counters.filter((c) => c.id !== counterId); //We do not remove the counter from state - we only make a new array after the event.
    this.setState({ counters });
  };

  handleReset = () => {
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters });
  };

  render() {
    return (
      <React.Fragment>
        {/* <NavBar totalCounters={this.state.counters.filter(c => c.value>0).length}></NavBar> */}

        {/* <main className="container">

        </main> */}
        <main id="primary" className="site-main">
          <section className="section">
            <div className="container is-max-widescreen py-4 ">
              <div className="columns is-multiline is-centered">
                <div className="column is-10-tablet is-9-desktop">
                  <article
                    id="post-11"
                    className="post-11 page type-page status-publish hentry"
                  >
                    <Header />

                    <div className="entry-content content mt-6">
                      <CalculatorList
                        counters={this.state.counters} // Instead of passing each prop separately, we pass the whole counters object.
                        onReset={this.handleReset}
                        onIncrement={this.handleIncrement}
                        onDecrement={this.handleDecrement}
                        onDelete={this.handleDelete}
                      />
                    </div>
                  </article>{" "}
                </div>
              </div>
            </div>
          </section>
        </main>
      </React.Fragment>
    );
  }
}

export default App;
