import React, { Component } from "react";
import Selector from "./selector";

class Counter extends React.Component {
  state = {
    selector: [
      { id: 1, lab: "Error type", opt1: "Relative", opt2: "Absolute" },
      { id: 2, lab: "Number notation", opt1: "Standard", opt2: "Scientific" },
      {
        id: 3,
        lab: "Error notation",
        opt1: "Standard ±",
        opt2: "Shorthand ()",
      },
    ],
  };
  ///////////////////////////////////////////////////////////
  //[LOCAL STATE OF COMPONENT - WHEN NOT INTENDED TO BE MODIFIED]
  //   state = { //local state- cannot be updated do "Reset" does not work.
  //     //state includes data that is local or private to the component
  //     //other components cannot access to that state
  //     //component may not have state, it may take all the data from props
  //     value: this.props.counter.value,
  //     //props includes data that we give to the component
  //     //props is read-only - we cannot assign to the value of the object
  //     //to modiufy something we need to reach the state

  //     //!! The component that owns a piece of the state, should be the one modifying it!!
  //     //!! So, in this case the Counters component owns the state "counters" so it should modify it!!
  //     // Cut, the state is private to the component, so how to modity it with another (Counter) component?
  //     // Counter component will raise the event
  //   };
  ///////////////////////////////////////////////////////////
  //   //[RENDERING LIST OF COMPONENTS (TAGS)]
  //   state = {
  //     tags: ["tag1", "tag2", "tag3"],
  //   };
  //     renderTags() {
  //       if (this.state.tags.length === 0) return <p>There are no tags!</p>;
  //       return (
  //         <ul>
  //           {this.state.tags.map((tag) => (
  //             <li key={tag}>{tag}</li>
  //           ))}
  //         </ul>
  //       );
  //     }
  ///////////////////////////////////////////////////////////
  //   //[RENDERING LIST OF COMPONENTS (TAGS)]
  //   constructor() {
  //     super(); //to have the
  //     this.handleIncrement = this.handleIncrement.bind(this);
  //   }
  //Functions in javaScript are objects so they have objects and methods.
  //Above is done to get access to the Counter object in function.
  //We will be able to update "state" property.
  //Solution to bind event handlers to "This"
  //There is another way of doing this:
  //   handleIncrement() {
  //     console.log("Increment clicked", this);
  //   }
  ///////////////////////////////////////////////////////////
  //   //[CONTROLLED COMPONENT]
  //Component is controlled by its parent - no local state should exist!
  //Same in Counters component.

  //onSubmit={this.handleSubmit}
  // Render method
  render() {
    return (
      // <div className="Container">
      // <div className="row">
      <div className="col-sm-4">
        <Selector
          key={this.state.selector.id}
          selector={this.state.selector}
        ></Selector>
        <button
          onClick={() => this.props.onDelete(this.props.counter.id)} //onDelete is a property of Counter component called in Counters component.
          // Here only the property onDelete of the counter, defined in counters component is called - <raising event handlers>
          // The reference must be given as an arrow function, as we need to pass an id of the counter to be deleted.
          // If instead of "this.props.counter.id" we would add 1, always counter of id 1 would be deleted.
          className="btn btn-danger m-2"
        >
          Delete
        </button>
      </div>
    );
  }

  // Method to change button classes on click to make it a selector.
  changeButtonClass() {
    let classes = "btn btn-sm btn-";
    classes += this.props.counter.value === 0 ? "primary" : "secondary"; // If counter value is 0 add "Warning", else add "primary".
    return classes;
  }

  // Method to format counter based on value.
  formatCount() {
    // Object destructuring - picking a single property of an object and store it in a const.
    const { value: value } = this.props.counter;
    return value === 0 ? "Zero" : value; // Instead of plain text "Zero" it is also possible to return a JSX expression: <h1>Zero</h1>.
    // Constant (const) can also store JSX expression. const x = <h1>Zero</h1>
  }

  ///////////////////////////////////////////////////////////
  //   //[USAGE OF <componentDidMount>]
  //   // Called after component is rendered into the DOM. Place to call data form the server.
  //   componentDidMount() {
  // //Ajax Call
  //     this.setState({data from the server})
  //   }
}

export default Counter;
