import React, { Component } from "react";

class NumForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };

    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  //   handleSubmit(event) {
  //     alert("Podano następujące imię: " + this.state.value);
  //     event.preventDefault();
  //   }

  //onSubmit={this.handleSubmit}

  render() {
    // const { id, lab, plHold, opt2 } = this.props.selector;

    console.log("worked)");
    return (
      <form>
        <label className="w-100">Number to format:</label>
        <div className="input-group">
          <input
            type="text"
            placeholder="1.23E+3 or 1230"
            className="form-control"
            value={this.state.value}
            onChange={this.handleChange}
          />
          <br />
          {/* <input type="submit" value="Wyślij" /> */}
          <small className="form-text text-muted">
            Use dot [.] or comma [,] as a decimal separator.
          </small>
        </div>
      </form>
    );
  }
}

export default NumForm;
