import React, { Component } from "react";
import Counter from "./counter"; // Import a child component from "components" folder. It actually adds new html tag that we can use in JSX.

class Counters extends React.Component {
  render() {
    const { onReset, counters, onDelete, onIncrement, onDecrement } =
      this.props; // instead of repeating this.props.counter.(...) we define a constant
    return (
      <div>
        <button className="btn btn-primary btn-small m-2" onClick={onReset}>
          Reset
        </button>
        {counters.map((counter) => (
          <Counter
            key={counter.id}
            onDelete={onDelete} // here we only refer to onDelete property set in parent App.jsx, same as we did in counter component.
            onIncrement={onIncrement}
            onDecrement={onDecrement}
            // value={counter.value}
            // id={counter.id}
            counter={counter} //counter object is carrying all the data about the counter - do not need to pass all the props separately
          >
            {/* <h4>Counter #{counter.id}</h4> */}
          </Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
