import React, { Component } from "react";
import Prepender from "./prepender";

class NumForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };

    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  //   handleSubmit(event) {
  //     alert("Podano następujące imię: " + this.state.value);
  //     event.preventDefault();
  //   }

  //onSubmit={this.handleSubmit}

  printPrepender(sign) {
    // if (isLoggedIn) {
    //   return <UserGreeting />;
    // }
    // return <GuestGreeting />;
    return sign != "" ? <Prepender sign={sign} /> : null;
  }

  render() {
    const { id, lab, plHold, prep, sign } = this.props.form;

    return (
      <form>
        <label className="w-100">{lab}</label>
        <div className="input-group">
          {/* <Prepender sign={sign} /> */}
          printPrepender(sign)
          <input
            type="text"
            placeholder={plHold}
            className="form-control"
            value={this.state.value}
            onChange={this.handleChange}
          />
          {/* <input type="submit" value="Wyślij" /> */}
        </div>
        <small className="form-text text-muted">
          Use dot [.] or comma [,] as a decimal separator.
        </small>
      </form>
    );
  }
}

export default NumForm;
