import React, {Component} from 'react';
import Header from './components/header';
import './App.css';
import CalculatorList from './components/calculator_list';

//Now we want to exchange data between two child components: Counters and Navbar (to display number of items)
// There is no parent-child relationship between the two
//We need to raise the state of Counters component up to App

class App extends Component {

  state = {
    counters: [
      { id: 1, value: 4 },
      // { id: 2, value: 0 },
      // { id: 3, value: 0 },
      // { id: 4, value: 0 },
    ],
  };

  handleIncrement = (counter) => {
    // this.state.count++; // we cannot change value of count directly like this - React will not be aware that it changed
    const counters = [...this.state.counters]; // ... is cloning array of counters from state to var "counters"
    const index = counters.indexOf(counter); // find index of counter within counters (in state)
    counters[index] = { ...counter }; //to clone the counter at exact location - counter object in state remains unchanged
    counters[index].value++;
    this.setState({ counters }); //update state
  };

  handleDecrement = (counter) => {
    // this.state.count++; // we cannot change value of count directly like this - React will not be aware that it changed
    const counters = [...this.state.counters]; // ... is cloning array of counters from state to var "counters"
    const index = counters.indexOf(counter); // find index of counter within counters (in state)
    counters[index] = { ...counter }; //to clone the counter at exact location - counter object in state remains unchanged
    counters[index].value -= counters[index].value>0 ? 1: 0;
    // counters[index].value === 0 ? counters[index].value-- : counters[index].value;
    // classes += this.props.counter.value === 0 ? "warning" : "primary";
    this.setState({ counters }); //update state
  };

  handleDelete = (counterId) => {
    // console.log("Delete event handled.", counterId);
    const counters = this.state.counters.filter((c) => c.id !== counterId); //We do not remove the counter from state - we only make a new array after the event.
    this.setState({ counters });
  };

  handleReset = () => {
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters });
  };

  render(){
    return (
      <React.Fragment>
        {/* <NavBar totalCounters={this.state.counters.filter(c => c.value>0).length}></NavBar> */}
        <Header/>
        <main className="container">
          <CalculatorList
            counters = {this.state.counters} // Instead of passing each prop separately, we pass the whole counters object.
            onReset = {this.handleReset}
            onIncrement = {this.handleIncrement}
            onDecrement = {this.handleDecrement}
            onDelete = {this.handleDelete}
          />
        </main>
        <main id="primary" class="site-main">
    <section class="section" style="padding-top:0rem;">
        <div class="container is-max-widescreen py-4 ">
            <div class="columns is-multiline is-centered">
                <div class="column is-10-tablet is-9-desktop">

<article id="post-11" class="post-11 page type-page status-publish hentry">
    <header>
		<h1 class="title is-spaced has-text-centered">About</h1>		    </header>


    <div class="entry-content content mt-6">

<p><em>Science and technology enthusiast with nearly 10 years of hands-on experience in Marine Engineering. Top notch in HVAC-R and heat transfer, with an expertise in LNG propulsion and vessels operating in polar waters.</em></p>



<p class="has-text-align-left">My name is Mateusz Mielcarek and I have been successfully engaged in the construction of ships for 10 years. The responsibilities I have undertaken range from initial design phase, functional and classification design, 3D engineering support and supervision to production support. I also have a broad experience in supervising 3rd party engineering services. I am highly specialized in:</p>



<ul><li>HVAC-R systems,</li><li>tank heating systems,</li><li>heat transfer and thermodynamics,</li><li>hazardous areas classification,</li><li>winterization of vessels.</li></ul>



<p>I have had the excellent opportunity to design systems related to LNG propulsion for many extremely different gas powered vessels, from a Container Vessel, FPSO to a Gas Carrier. I have been involved in the design of an Ice Breaking Vessel and other ships under Russian Maritime Register of Shipping and Russian Flag.</p>



<p>I currently have a position od Principal Engineer in Damen Engineering Gdansk, where my main responsibilities are:</p>



<ul><li>supervision of design of HVAC-R systems,</li><li>independent analyses, impact analyses and feasibility studies related to HVAC-R and heat transfer,</li><li>conceptual design, project development,</li><li>leading a small group of engineers,</li><li>mentoring a team of HVAC engineers.</li></ul>



<p>I hope that the experience I have gained over the years of my engineering career, which I share with you on this website, will help you solve your engineering problems, including those related to ship design. I wish I had had this kind of resource for detailed engineering knowledge when I started my professional career.</p>



<p>Sincerly Yours,Mateusz</p>



<p></p>
    </div>

</article>                </div>
            </div>
        </div>
    </section>

</main>
      </React.Fragment>
    );
  }
}

export default App;
