import React, { Component } from "react";

class NumForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };

    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  //   handleSubmit(event) {
  //     alert("Podano następujące imię: " + this.state.value);
  //     event.preventDefault();
  //   }

  //onSubmit={this.handleSubmit}

  render() {
    const { id, lab, plHold, prep } = this.props.form;

    return (
      <form>
        <label className="w-100">{lab}</label>
        <div className="input-group">
          {/* <div className={this.getPrependClasses()}>
            <span className="input-group-text">±</span>
          </div> */}
          <input
            type="text"
            placeholder={plHold}
            className="form-control"
            value={this.state.value}
            onChange={this.handleChange}
          />
          {/* <input type="submit" value="Wyślij" /> */}
        </div>
        <small className="form-text text-muted">
          Use dot [.] or comma [,] as a decimal separator.
        </small>
      </form>
    );
  }
  // Method to assign class to input-group-prepend and hide it if necessary.
  getPrependClasses() {
    let classes = "input-group-prepend ";
    classes += this.props.form.prep === 0 ? "d-none" : "";
    return classes;
  }
}

export default NumForm;
