import React, { Component } from "react";

class Counter extends React.Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"],
  };

  renderTags() {
    if (this.state.tags.length === 0) return <p>There are no tags!</p>;
    return (
      <ul>
        {this.state.tags.map((tag) => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }

  //   constructor() {
  //     super(); //to have the
  //     this.handleIncrement = this.handleIncrement.bind(this);
  //   }
  //Functions in javaScript are objects so they have objects and methods.
  //Above is done to get access to the Counter object in function.
  //We will be able to update "state" property.
  //Solution to bind event handlers to "This"
  //There is another way of doing this:
  //   handleIncrement() {
  //     console.log("Increment clicked", this);
  //   }

  handleIncrement = () => {
    console.log("Increment clicked", this);
  };
  //using an arrow function is cleaner solution

  render() {
    return (
      <div>
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button
          onClick={this.handleIncrement}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        {this.state.tags.length === 0 && "please create a new tag!"}
        {this.renderTags()}
      </div>
    );
  }

  getBadgeClasses() {
    let classes = "badge m-2 bg-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
